mapper.select:
SELECT p.name AS "productName", (Select code_name from code where code = detail_type ) AS "detailType", price AS "price", p.like_count AS "like", p."COMMENT" AS "comment", pd.name AS "detailName", b.code AS "brandCode", b.name AS "brandName", bi.img_path AS "brandImgPath", img_name AS "brandImgName"
,(CASE WHEN ll.id is not null then 'Y' else 'N' end ) AS "likeYN"
,'N' AS "likeYN"
FROM product p JOIN product_detail pd ON p.code=pd.product_code JOIN brand b ON p.brand_code=b.code JOIN brand_image bi ON p.brand_code=bi.brand_code
LEFT OUTER JOIN like_list ll ON p.code=ll.product_code AND ll.id=#{memberId}
WHERE p.code = #{productCode}

SELECT main.seq AS "seq", image.seq AS "imgSeq", image.review_seq AS "img_review_Seq", main.member_id AS "memberId", main.content AS "content", main.like_count AS "like", To_char(main.reg_date, 'YYYY.MM.DD') AS "regDate", image.img_origin_name AS "imgName", (image.img_path || image.img_origin_name) AS "imgPath", gup.imgCount AS "imgCount"
FROM review main INNER JOIN ( SELECT r.seq , ri.review_seq , COUNT(CASE WHEN r.seq = ri.review_seq THEN 1 END) AS imgCount FROM review r LEFT OUTER JOIN ( SELECT seq , review_seq , img_origin_name , img_path FROM review_image ) ri ON r.seq = ri.review_seq WHERE 1 = 1 GROUP BY ( r.seq , ri.review_seq)
) gup ON main.seq = gup.seq LEFT OUTER JOIN review_image image ON main.seq = image.review_seq WHERE main.product_code = '${productCode}' ORDER BY main.seq
